mapper.select:
select * from sys_department where status=1

	and companyId=#{companyId}

order by sortNumber

SELECT sd.*,psd.`deptName` as parentDeptName,sdt.`deptTypeName`,bc.`companyName` FROM sys_department sd,sys_department psd ,sys_dept_type sdt,biz_company bc WHERE sd.`parentId`=psd.`deptId` AND sd.`deptTypeId`=sdt.`deptTypeId` AND sd.`companyId`=bc.`companyId`
and sd.status=#{status}
and sd.status=3
and sd.status!=3
and sd.deptName like concat('%',#{deptName},'%')
ORDER BY sd.`creationDate` DESC limit #{startRow},#{page}
