mapper.select:
select * from sys_user  where loginName=#{loginName}
	and password=#{password}
	and status=1

mapper.update:
update sys_user SET
	modifuUser=#{modifyUser},
	modifyDate=now(),
	status=#{status}
	WHERE userId=#{userId}

mapper.select:
		select su.*,sd.deptName,sc.companyName,sr.roleName from sys_user su,sys_user_role sur,sys_department sd,biz_company sc,sys_role sr
		where su.userId=sur.userId and su.deptId=sd.deptId and sc.companyId=su.companyId and sur.roleId=sr.roleId

			and su.companyId =#{companyId}


			and su.deptId =#{deptId}


			and sur.roleId =#{roleId}


			and su.status=#{status}


			and su.userName like concat('%',#{userName},'%')

        limit #{startRow},#{pageSize}

select count(1) from sys_user su,sys_user_role sur
	where su.userId=sur.userId

		and su.companyId =#{companyId}


		and su.deptId =#{deptId}


		and sur.roleId =#{roleId}


		and su.status=#{status}


		and su.userName like concat('%',#{userName},'%')
